public class BucketSort { public static void main(String[] args){ double[] arr={0.897,0.565,0.656,0.1234,0.665,0.3434}; int n=arr.length; java.util.ArrayList<Double>[] buckets=new java.util.ArrayList[n]; for(int i=0;i<n;i++) buckets[i]=new java.util.ArrayList<Double>(); for(int i=0;i<n;i++){ int idx=(int)(arr[i]*n); buckets[idx].add(arr[i]); } for(int i=0;i<n;i++) java.util.Collections.sort(buckets[i]); int index=0; for(int i=0;i<n;i++) for(double val:buckets[i]) arr[index++]=val; System.out.println(java.util.Arrays.toString(arr)); }}