public class CountingSort { public static void main(String[] args){ int[] arr={4,2,2,8,3,3,1}; int n=arr.length; int max=arr[0]; for(int i=1;i<n;i++) if(arr[i]>max) max=arr[i]; int[] count=new int[max+1]; for(int i=0;i<n;i++) count[arr[i]]++; int index=0; for(int i=0;i<count.length;i++) while(count[i]-->0) arr[index++]=i; System.out.println(java.util.Arrays.toString(arr)); }}