public class RadixSort { public static int getMax(int[] arr){ int mx=arr[0]; for(int i=1;i<arr.length;i++) if(arr[i]>mx) mx=arr[i]; return mx; } public static void countSort(int[] arr,int exp){ int n=arr.length; int[] output=new int[n]; int i; int[] count=new int[10]; for(i=0;i<n;i++) count[(arr[i]/exp)%10]++; for(i=1;i<10;i++) count[i]+=count[i-1]; for(i=n-1;i>=0;i--){ output[count[(arr[i]/exp)%10]-1]=arr[i]; count[(arr[i]/exp)%10]--; } for(i=0;i<n;i++) arr[i]=output[i]; } public static void radixSort(int[] arr){ int m=getMax(arr); for(int exp=1;m/exp>0;exp*=10) countSort(arr,exp); } public static void main(String[] args){ int[] arr={170,45,75,90,802,24,2,66}; radixSort(arr); System.out.println(java.util.Arrays.toString(arr)); }}