public class ExponentialSearch { public static int binarySearch(int[] arr,int l,int r,int x){ while(l<=r){ int m=l+(r-l)/2; if(arr[m]==x) return m; else if(arr[m]<x) l=m+1; else r=m-1; } return -1; } public static int exponentialSearch(int[] arr,int x){ if(arr[0]==x) return 0; int i=1; while(i<arr.length && arr[i]<=x) i*=2; return binarySearch(arr,i/2,Math.min(i,arr.length-1),x); } public static void main(String[] args){ int[] arr={2,3,4,10,40}; int x=10; System.out.println(exponentialSearch(arr,x)); }}