public class InterpolationSearch { public static int interpSearch(int[] arr,int x){ int lo=0,hi=arr.length-1; while(lo<=hi && x>=arr[lo] && x<=arr[hi]){ int pos=lo+((hi-lo)*(x-arr[lo]))/(arr[hi]-arr[lo]); if(arr[pos]==x) return pos; if(arr[pos]<x) lo=pos+1; else hi=pos-1; } return -1; } public static void main(String[] args){ int[] arr={10,20,30,40,50}; int x=30; System.out.println(interpSearch(arr,x)); }}