public class RotateArray { public static void rotate(int[] arr,int d){ int n=arr.length; d=d%n; reverse(arr,0,d-1); reverse(arr,d,n-1); reverse(arr,0,n-1); } public static void reverse(int[] arr,int l,int r){ while(l<r){ int temp=arr[l]; arr[l]=arr[r]; arr[r]=temp; l++; r--; } } public static void main(String[] args){ int[] arr={1,2,3,4,5}; rotate(arr,2); System.out.println(java.util.Arrays.toString(arr)); }}